    for header in headers:
        html += f"<th class='light-professional-header'>{header}</th>"
    html += "</tr></thead>"

    # Body
    html += "<tbody>"
    for row in data:
        html += "<tr class='light-professional-row'>"
        for i, cell in enumerate(row):
            if i == 0:  # First column (usually Case ID)
