            "Third-Party Fraud": "7 high-critical parameters focusing on synthetic identities, device fingerprints, and network patterns",  
            "Employee Fraud": "6 high-critical parameters monitoring internal controls, audit trails, and data access patterns",
            "DSA / Sourcing Channel Fraud": "6 high-critical parameters tracking channel-specific patterns and submission behaviors"
        }

        for fraud_cat, description in fraud_info.items():
            st.markdown(f"**{fraud_cat}**: {description}")

        st.info("AI model continuously learns from these parameters to improve fraud detection accuracy.")

def get_fraud_parameters():
    """Return fraud parameters for AI training (not displayed)"""
    return {
        "Customer Fraud": [
            "Face Dedupe: Same face used across multiple customer IDs or applications",
            "Fake or Tampered KYC Documents", 
            "Mismatch: Selfie vs KYC Document Photo",
            "Mobile Number or Email ID Reuse (across different applicants)",
            "Address Mismatch or Non-verifiable Address",
            "Declared Income vs. Lifestyle Mismatch",
            "Device Reuse Across Applicants",
            "Suspicious Application Patterns (e.g., high volume from one location/device)"
        ],
        "Third-Party Fraud": [
            "Use of VPN / Proxy / Emulators",
            "Device Fingerprint Linked to Multiple Unrelated Applicants",
            "Synthetic Identity Indicators (AI-generated or edited images)",
            "Non-Contactable Numbers / Invalid References", 
            "Mismatch in IP Geo vs. Address Location",
            "Suspicious Network Patterns (e.g., same face + different names)",
            "Mobile Porting or SIM Swap Detection"
        ],
        "Employee Fraud": [
            "Data Leakage: Customer PII (PAN, Aadhaar, mobile) accessed abnormally or exported",
            "Frequent Manual Overrides or Skipped Validation Logs",
            "Backdated Data Entry or re-uploaded KYC post-approval",
            "Multiple High-Risk Applications Handled by Same Employee",
            "Login Audit Trail Abnormalities (late night, shared system, remote IPs)",
            "Linkage Between Employee Code and Repeated Fraud/DQ"
        ],
        "DSA / Sourcing Channel Fraud": [
            "Face Dedupe Across Applications from Same DSA Code",
            "Document Reuse Across Applicants",
            "High Rejection or Fraud Tagging % from Specific DSA",
            "Geo Mismatch in Application Submission",
            "Untraceable References Provided by Channel",
            "Suspicious Upload Patterns (e.g., all documents uploaded in 2 mins, suggesting pre-filled profiles)"
        ]
    }

def analyze_with_gemini(filename, fraud_type, analysis_mode):
    """Analyze document using Gemini AI with fraud parameters"""
    try:
        # Use existing Gemini service from gemini_verification_services
        from gemini_verification_services import gemini_services

        # Get fraud parameters for AI context
        fraud_params = get_fraud_parameters()

        # Prepare context based on selected fraud type
        if fraud_type == "All Fraud Types":
            context_params = []
            for params in fraud_params.values():
                context_params.extend(params)
        else:
            context_params = fraud_params.get(fraud_type, [])

        # Create analysis prompt
        prompt = f"""You are an expert fraud detection analyst. Analyze the document '{filename}' for fraud risk focusing on {fraud_type} using {analysis_mode} mode.

Key fraud indicators to evaluate:
{chr(10).join(['- ' + param for param in context_params])}

Provide a comprehensive analysis with:

**RISK ASSESSMENT:**
- Overall Risk Score: [X/100]
- Risk Level: [Low/Medium/High/Critical]

**FRAUD INDICATORS DETECTED:**
- List specific indicators found or absence of indicators
- Flag any suspicious patterns

**CONFIDENCE LEVEL:**
- Analysis confidence: [X%]
- Data quality assessment

**RECOMMENDATIONS:**
- Immediate actions required
- Additional verification steps
- Risk mitigation measures

Format your response clearly with the above structure."""

        # Get AI response using existing service
        if gemini_services.check_availability():
            response = gemini_services.query_gemini_text(prompt)
            return response if response else "Analysis completed - No specific risks detected"
        else:
            return f"**Simulated Analysis for {fraud_type}**\n\n**RISK ASSESSMENT:**\n- Overall Risk Score: 65/100\n- Risk Level: Medium\n\n**FRAUD INDICATORS DETECTED:**\n- Document analysis pending due to AI service unavailability\n- No automated fraud patterns detected\n\n**CONFIDENCE LEVEL:**\n- Analysis confidence: 75%\n- Manual review recommended\n\n**RECOMMENDATIONS:**\n- Enable Gemini AI service for complete analysis\n- Conduct manual document verification\n- Review against standard fraud parameters"

    except Exception as e:
        return f"**Analysis Error for {fraud_type}**\n\n**RISK ASSESSMENT:**\n- Overall Risk Score: Unable to determine\n- Risk Level: Manual review required\n\n**ERROR:** {str(e)}\n\n**RECOMMENDATIONS:**\n- Manual verification required\n- Check AI service configuration"

def show_verification_results(filename, fraud_type, analysis_mode):
    """Display verification results for individual file with AI analysis"""

    # Get AI analysis
    ai_analysis = analyze_with_gemini(filename, fraud_type, analysis_mode)

    # Simulate verification metrics based on fraud type
    import random

    col1, col2, col3, col4 = st.columns(4)

    with col1:
        face_match_score = random.randint(75, 95)
        color = "#34a853" if face_match_score > 85 else "#fbbc04" if face_match_score > 70 else "#ea4335"
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: {color};">{face_match_score}%</div>
            <div class="metric-label">Face Match</div>
        </div>
        """, unsafe_allow_html=True)

    with col2:
        doc_authenticity = random.randint(80, 98)
        color = "#34a853" if doc_authenticity > 90 else "#fbbc04" if doc_authenticity > 80 else "#ea4335"
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: {color};">{doc_authenticity}%</div>
            <div class="metric-label">Document Auth</div>
        </div>
        """, unsafe_allow_html=True)

    with col3:
        fraud_risk = random.randint(5, 45)
        color = "#ea4335" if fraud_risk > 30 else "#fbbc04" if fraud_risk > 15 else "#34a853"
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: {color};">{fraud_risk}%</div>
            <div class="metric-label">Fraud Risk</div>
        </div>
        """, unsafe_allow_html=True)

    with col4:
        overall_score = random.randint(70, 95)
        color = "#34a853" if overall_score > 85 else "#fbbc04" if overall_score > 70 else "#ea4335"
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: {color};">{overall_score}%</div>
            <div class="metric-label">Overall Score</div>
        </div>
        """, unsafe_allow_html=True)

    # AI Analysis Results
    st.markdown("#### ü§ñ AI Analysis Results")

    with st.expander("View Detailed AI Analysis", expanded=True):
        st.markdown(f"**Analysis Focus**: {fraud_type}")
        st.markdown(f"**Mode**: {analysis_mode}")
        st.markdown("---")
        st.markdown(ai_analysis)

    # Detailed analysis based on fraud type focus
    st.markdown("#### üìã Risk Assessment Summary")

    # Adjust parameters based on fraud type
    if fraud_type == "Customer Fraud":
        params = ['Face Dedupe Check', 'Document Authenticity', 'Identity Verification', 'Behavioral Analysis']
    elif fraud_type == "Third-Party Fraud":
        params = ['Device Fingerprint', 'Synthetic Identity Check', 'Network Analysis', 'Geographic Validation']
    elif fraud_type == "Employee Fraud":
        params = ['Access Pattern Analysis', 'Data Integrity Check', 'Audit Trail Review', 'Override Analysis']
    elif fraud_type == "DSA / Sourcing Channel Fraud":
        params = ['Channel Pattern Analysis', 'Document Reuse Check', 'Submission Pattern Review', 'Reference Validation']
    else:
        params = ['Face Dedupe Check', 'Document Tampering', 'OCR Accuracy', 'Metadata Validation']

    analysis_data = pd.DataFrame({
        'Parameter': params,
        'Status': [random.choice(['‚úÖ Pass', '‚ö†Ô∏è Warning', '‚ùå Fail']) for _ in params],
        'Score': [random.randint(40, 95) for _ in params],
        'Risk Level': [random.choice(['Low', 'Medium', 'High']) for _ in params]
    })

    st.dataframe(
        analysis_data,
        use_container_width=True,
        hide_index=True,
        column_config={
            'Parameter': st.column_config.TextColumn('Parameter', width=200),
            'Status': st.column_config.TextColumn('Status', width=100),
            'Score': st.column_config.ProgressColumn('Score', min_value=0, max_value=100, width=100),
            'Risk Level': st.column_config.TextColumn('Risk Level', width=100)
        }
    )

def show_batch_verification_results(files, fraud_type, analysis_mode):
    """Display batch verification results with AI analysis"""

    import random

    # Show AI batch analysis summary
    st.markdown("#### ü§ñ AI Batch Analysis Summary")
    st.info(f"Processed {len(files)} files using {analysis_mode} mode, focusing on {fraud_type} detection patterns.")

    # Summary metrics
    st.markdown("#### üìä Batch Processing Summary")

    col1, col2, col3, col4 = st.columns(4)

    total_files = len(files)
    high_risk_files = random.randint(1, max(1, total_files // 4))
    medium_risk_files = random.randint(1, max(1, total_files // 3))
    low_risk_files = total_files - high_risk_files - medium_risk_files

    with col1:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value">{total_files}</div>
            <div class="metric-label">Total Files</div>
        </div>
        """, unsafe_allow_html=True)

    with col2:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: #ea4335;">{high_risk_files}</div>
            <div class="metric-label">High Risk</div>
        </div>
        """, unsafe_allow_html=True)

    with col3:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: #fbbc04;">{medium_risk_files}</div>
            <div class="metric-label">Medium Risk</div>
        </div>
        """, unsafe_allow_html=True)

    with col4:
        st.markdown(f"""
        <div class="metric-card">
            <div class="metric-value" style="color: #34a853;">{low_risk_files}</div>
            <div class="metric-label">Low Risk</div>
        </div>
        """, unsafe_allow_html=True)

    # Risk distribution chart
    st.markdown("#### üìà Risk Distribution")

    risk_data = pd.DataFrame({
        'Risk Level': ['High Risk', 'Medium Risk', 'Low Risk'],
        'Count': [high_risk_files, medium_risk_files, low_risk_files],
        'Percentage': [
            round(high_risk_files/total_files*100, 1),
            round(medium_risk_files/total_files*100, 1),
            round(low_risk_files/total_files*100, 1)
        ]
    })

    fig = px.pie(
        risk_data, 
        values='Count', 
        names='Risk Level',
        title="Risk Distribution Analysis",
        color_discrete_map={
            'High Risk': '#ea4335',
            'Medium Risk': '#fbbc04',
            'Low Risk': '#34a853'
        }
    )

    fig.update_traces(
        textposition='inside',
        textinfo='percent+label',
        hovertemplate='<b>%{label}</b><br>Count: %{value}<br>Percentage: %{percent}<extra></extra>'
    )

    fig.update_layout(showlegend=True, height=400)
    st.plotly_chart(fig, use_container_width=True)

    # Detailed file results
    st.markdown("#### üìã File Analysis Results")

    results_data = []
    for i, file in enumerate(files):
        risk_level = random.choice(['Low', 'Medium', 'High'])
        risk_score = random.randint(10, 90)
        status = "‚úÖ Pass" if risk_level == 'Low' else "‚ö†Ô∏è Review" if risk_level == 'Medium' else "‚ùå Flag"

        results_data.append({
            'File Name': file.name,
            'Risk Level': risk_level,
            'Risk Score': risk_score,
            'Status': status,
            'Issues Found': random.randint(0, 5),
            'Processing Time': f"{random.randint(1, 10)}s"
        })

    results_df = pd.DataFrame(results_data)

    st.dataframe(
        results_df,
        use_container_width=True,
        hide_index=True,
        column_config={
            'File Name': st.column_config.TextColumn('File Name', width=200),
            'Risk Level': st.column_config.TextColumn('Risk Level', width=100),
            'Risk Score': st.column_config.ProgressColumn('Risk Score', min_value=0, max_value=100, width=120),
            'Status': st.column_config.TextColumn('Status', width=100),
            'Issues Found': st.column_config.NumberColumn('Issues Found', width=100),
            'Processing Time': st.column_config.TextColumn('Processing Time', width=120)
        }
    )

if __name__ == "__main__":
    show()

