    if spinner_type == "mascot":
        svg_content = get_tathya_mascot_svg()
        spinner_html = f"""
        <div style="display: flex; flex-direction: column; align-items: center; padding: 20px;">
            {svg_content}
            <div style="margin-top: 15px; font-size: 16px; color: #2E86AB; font-weight: 500;">
                üîé {message}
            </div>
            <div style="margin-top: 5px; font-size: 12px; color: #666; animation: pulse 2s ease-in-out infinite;">
                Tathya Investigation Intelligence
            </div>
        </div>
        """

    elif spinner_type == "verification":
        svg_content = get_verification_spinner_svg()
        spinner_html = f"""
        <div style="display: flex; flex-direction: column; align-items: center; padding: 20px;">
            {svg_content}
            <div style="margin-top: 15px; font-size: 16px; color: #2E86AB; font-weight: 500;">
                ‚úÖ {message}
            </div>
        </div>
        """

    elif spinner_type == "ai":
        svg_content = get_ai_analysis_spinner_svg()
        spinner_html = f"""
        <div style="display: flex; flex-direction: column; align-items: center; padding: 20px;">
            {svg_content}
            <div style="margin-top: 15px; font-size: 16px; color: #6C5CE7; font-weight: 500;">
                ü§ñ {message}
            </div>
        </div>
        """

    # Create container with animation
    container = st.empty()

    # Add CSS for additional animations
    st.markdown("""
    <style>
    @keyframes pulse {
        0%, 100% { opacity: 1; }
        50% { opacity: 0.6; }
    }

    .spinner-container {
        background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
        border-radius: 15px;
        box-shadow: 0 4px 15px rgba(0,0,0,0.1);
        margin: 10px 0;
    }
    </style>
    """, unsafe_allow_html=True)

    # Display spinner in container
    container.markdown(f'<div class="spinner-container">{spinner_html}</div>', unsafe_allow_html=True)

    # If duration is specified, wait and then clear
    if duration:
        time.sleep(duration)
        container.empty()

    return container

def create_loading_states():
    """Create different loading state demonstrations"""

    st.markdown("## üé® Animated Loading Spinners with Brand Mascot")
    st.markdown("Interactive demonstrations of custom animated spinners for different processes:")

    col1, col2, col3 = st.columns(3)

    with col1:
        if st.button("üîé Tathya Mascot Spinner", use_container_width=True):
            show_animated_spinner("mascot", "Analyzing customer data...", 3)
            st.success("Analysis complete!")

    with col2:
        if st.button("‚úÖ Verification Spinner", use_container_width=True):
            show_animated_spinner("verification", "Verifying PAN details...", 3)
            st.success("Verification complete!")

    with col3:
        if st.button("ü§ñ AI Analysis Spinner", use_container_width=True):
            show_animated_spinner("ai", "AI processing documents...", 3)
            st.success("AI analysis complete!")

# Context managers for easy integration
class TathyaSpinner:
    """Context manager for Tathya spinner"""

    def __init__(self, spinner_type="mascot", message="Processing..."):
        self.spinner_type = spinner_type
        self.message = message
        self.container = None

    def __enter__(self):
        self.container = show_animated_spinner(self.spinner_type, self.message)
        return self

    def __exit__(self, exc_type, exc_val, exc_tb):
        if self.container:
            self.container.empty()

# Quick access functions
def with_mascot_spinner(message="Processing..."):
    """Decorator/context manager for mascot spinner"""
    return TathyaSpinner("mascot", message)

def with_verification_spinner(message="Verifying..."):
    """Decorator/context manager for verification spinner"""
    return TathyaSpinner("verification", message)

def with_ai_spinner(message="AI Processing..."):
    """Decorator/context manager for AI spinner"""
    return TathyaSpinner("ai", message)

