def show_tile_section(title, color, items):
    """Show a modern tile-based section with collapsible header"""

    # Custom CSS for tiles and animations
    st.markdown(f"""
    <style>
    .tile-section-{color.replace('#', '')} {{
        background: linear-gradient(135deg, {color}10 0%, {color}05 100%);
        border: 2px solid {color}30;
        border-radius: 15px;
        margin: 1.5rem 0;
        box-shadow: 0 8px 25px rgba(0,0,0,0.08);
        transition: all 0.3s ease;
    }}

    .tile-grid {{
        display: grid;
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
        gap: 1.2rem;
        padding: 1.5rem;
    }}

    .verification-tile {{
        background: white;
        border: 2px solid {color}20;
        border-radius: 12px;
        padding: 1.5rem;
        text-align: center;
        cursor: pointer;
        transition: all 0.3s ease;
        box-shadow: 0 4px 15px rgba(0,0,0,0.05);
        position: relative;
        overflow: hidden;
    }}

    .verification-tile:hover {{
        transform: translateY(-3px);
        box-shadow: 0 12px 30px rgba(0,0,0,0.15);
        border-color: {color}60;
        background: linear-gradient(135deg, white 0%, {color}08 100%);
    }}

    .tile-icon {{
        font-size: 2.5rem;
        margin-bottom: 0.8rem;
        display: block;
    }}

    .tile-title {{
        color: {color};
        font-weight: 700;
        font-size: 1.1rem;
        margin: 0;
        line-height: 1.3;
    }}

    .section-header {{
        background: linear-gradient(135deg, {color}20 0%, {color}40 100%);
        color: white;
        padding: 1.2rem 1.8rem;
        border-radius: 12px 12px 0 0;
        font-weight: 700;
        font-size: 1.3rem;
        text-shadow: 1px 1px 3px rgba(0,0,0,0.3);
        cursor: pointer;
        user-select: none;
        transition: all 0.3s ease;
    }}

    .section-header:hover {{
        background: linear-gradient(135deg, {color}30 0%, {color}50 100%);
        transform: translateY(-1px);
    }}
    </style>
    """,
                unsafe_allow_html=True)

    # Create collapsible section
    with st.expander(title, expanded=False):
        # Create tile grid
        st.markdown(f'<div class="tile-grid">', unsafe_allow_html=True)

        # Create columns for responsive grid
        num_cols = min(4, len(items))  # Max 4 tiles per row
        cols = st.columns(num_cols)

        for i, (name, icon) in enumerate(items):
            col_idx = i % num_cols
            with cols[col_idx]:
                # Create clickable tile
                tile_key = f"tile_{name.lower().replace(' ', '_')}"

                # Use button for tile interaction
                if st.button(f"{icon}\n{name}",
                             key=tile_key,
                             use_container_width=True,
                             help=f"Click to use {name}"):
                    # Store selected tool in session state
                    st.session_state.selected_tool = name
                    st.session_state.selected_color = color
                    st.session_state.selected_section = title

        st.markdown('</div>', unsafe_allow_html=True)

